import { useState } from "react";
import { Link } from "react-router-dom";

function Categories () {
    let currentTarget = false;
    return (
        <div className="categories-container">
            <Link 
                to="/breakfast"
                onClick={(e) => {
                    currentTarget = true;
                    if(currentTarget){
                        e.target.style.backgroundColor = "#ffff66"
                    }
                    currentTarget = false;
                }}
            >
                Breakfast
            </Link>
            <Link
                to="/appetizers"
                onClick={(e) => {
                    currentTarget = true;
                    if(currentTarget){
                        e.target.style.backgroundColor = "#ffff66"
                    }
                    currentTarget = false;
                }}
            >
                Appetizers
            </Link>
            <Link
                to="/entrees"
            >
                Entrees
            </Link>
            <Link 
                to="/american"
            >
                American
            </Link>
            <Link
                to="/filipino"
            >
                Filipino
            </Link>
            <Link
                to="/pizza"
            >
                Pizza
            </Link>
            <Link
                to="/soups-salads"
            >
                Soups & Salads
            </Link>
        </div>
    )
}

export default Categories;
