import { useState } from "react";
import { Link } from "react-router-dom";

function Categories () {
    const [clickStyle, setClickStyle] = useState(false);
    const handleClick = () => {
        setClickStyle(true)
    }
    return (
        <div className="categories-container">
            <Link 
                to="/breakfast"
                onClick={handleClick}
                style={{
                    backgroundColor: clickStyle ? "#FFFF66" : "",
                    fontWeight: clickStyle ? "bold" : ""
                }}
            >
                Breakfast
            </Link>
            <Link
                to="/appetizers"
                onClick={handleClick}
                style={{
                    backgroundColor: clickStyle ? "#FFFF66" : "",
                    fontWeight: clickStyle ? "bold" : ""
                }}
            >
                Appetizers
            </Link>
            <Link
                to="/entrees"
            >
                Entrees
            </Link>
            <Link 
                to="/american"
            >
                American
            </Link>
            <Link
                to="/filipino"
            >
                Filipino
            </Link>
            <Link
                to="/pizza"
            >
                Pizza
            </Link>
            <Link
                to="/soups-salads"
            >
                Soups & Salads
            </Link>
        </div>
    )
}

export default Categories;
